CREATE TABLE policy_info (
    id integer NOT NULL,
    state_id integer NOT NULL,
    policy_grade character varying(255),
    climate_plan character varying(255),
    portfolio_standard character varying(255),
    pace character varying(255),
    clean_vehicle character varying(255),
    green_pricing character varying(255),
    home_solar character varying(255),
    community_solar character varying(255),
    community_choice character varying(255),
    energy_standard character varying(255),
    utility_board character varying(255),
    resident_count integer,
    resident_mwh integer
);


ALTER TABLE policy_info ADD CONSTRAINT policy_info_pk PRIMARY KEY (id);

ALTER TABLE policy_info ADD CONSTRAINT policy_info_fk0 FOREIGN KEY (state_id) REFERENCES public.state(id);